import React from "react";
import Joi from "joi-browser";
import axios from "axios";
import Form from "./authentication/Form";
import { getMovie, saveMovie } from "../services/fakeMovieService";
import { getGenres } from "../services/fakeGenreService";

class MovieForm extends Form {
  state = {
    data: { title: "", genre: {}, numberInStock: "", dailyRentalRate: "" },
    genres: [],
    errors: {},
  };

  schema = {
    _id: Joi.string(),
    title: Joi.string().required().label("Title"),
    genre: Joi.object({ id: Joi.number(), name: Joi.string() })
      .required()
      .label("Genre"),
    genre: Joi.string().label("Genre"),
    numberInStock: Joi.number()
      .required()
      .min(0)
      .max(100)
      .label("Number in stock"),
    dailyRentalRate: Joi.number().required().min(0).max(10).label("Rate"),
  };

  componentDidMount() {
    const genres = getGenres();
    this.setState({ genres });

    const movieId = this.props.match.path;
    if (movieId === "/movies/new") return;

    const movie = getMovie(movieId);
    if (!movie) return this.props.history.replace("/not-found");
    this.setState({ data: this.mapToViewModel(movie) });
  }

  mapToViewModel(movie) {
    return {
      _id: movie._id,
      title: movie.title,
      genre: { id: movie.genre._id, name: movie.genre.name },
      numberInStock: movie.numberInStock,
      dailyRentalRate: movie.dailyRentalRate,
    };
  }

  doSubmit = () => {
    // Calling server
    // saveMovie(this.state.data);
    axios.post("/movies", this.state.data).then((response) => {
      // saveMovie(this.state.data);
      console.log(response.data);
    });
    this.props.history.push("/movies");
  };

  render() {
    const { genres: options } = this.state;
    return (
      <div className="w-50 my-0 mx-auto">
        <h2 className="mb-4">Create Movie</h2>
        <form onSubmit={this.handleSubmit}>
          {this.renderInput("title", "Title", "text", true)}
          {this.renderSelect("genreId", "Genre", options)}
          {this.renderInput("numberInStock", "Number in stock")}
          {this.renderInput("dailyRentalRate", "Rate")}
          {this.renderButton("Save")}
        </form>
      </div>
    );
  }
}

export default MovieForm;
