import React, { Component } from "react";
import { Link } from "react-router-dom";

class Navbar extends Component {
  state = {
    navBarLinks: [
      { id: 1, name: "Movies", path: "/" },
      { id: 2, name: "Customers", path: "/customers" },
      { id: 3, name: "Rental", path: "/rental" },
    ],
    selectedLink: 0,
  };

  handleSelect = (link) => {
    console.log(link);
    this.setState({ selectedLink: link.id });
    console.log(this.state.selectedLink);
  };

  render() {
    return (
      <nav class="navbar navbar-expand-md navbar-light bg-light">
        <a class="navbar-brand" href="#">
          HDLibrary
        </a>
        <button
          class="navbar-toggler"
          type="button"
          data-toggle="collapse"
          data-target="#navbarNav"
          aria-controls="navbarNav"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span class="navbar-toggler-icon"></span>
        </button>
        <div class="collapse navbar-collapse" id="navbarNav">
          <ul class="navbar-nav">
            {this.state.navBarLinks.map((link) => (
              <li
                key={link.id}
                onClick={() => this.handleSelect(link)}
                class="nav-item active"
              >
                <Link class="nav-link" to={link.path}>
                  {link.name} <span class="sr-only">(current)</span>
                </Link>
              </li>
            ))}
          </ul>
        </div>
      </nav>
    );
  }
}

export default Navbar;
