import React from "react";
import Form from "./authentication/Form";
import Joi from "joi-browser";

class MovieForm extends Form {
  state = {
    data: { title: "", genreId: "", NumberInStock: "", dialyRentalRate: "" },
    genres: [],
    errors: {},
  };

  schema = {
    _id: Joi.string(),
    title: Joi.string()
      .required()
      .label("Movie title"),
    genreId: Joi.string()
      .required()
      .label("Genre"),
    NumberInStock: Joi.number()
      .string()
      .required()
      .label("Number in stock"),
    rate: Joi.string()
      .required()
      .label("Rate"),
  };

  doSubmit = () => {
    // Call Server
    console.log("Submited to the server!");
  };

  render() {
    return (
      <div className="w-50 my-0 mx-auto">
        <h2 className="mb-4">Create new movie</h2>
        <form onSubmit={this.handleSubmit}>
          {this.renderInput("title", "Title", true)}
          {this.renderInputSelect("genre", "Genre")}
          {this.renderInput("numberInStock", "Number in stock")}
          {this.renderInput("rate", "Rate")}
          {this.renderButton("Save")}
        </form>
      </div>
    );
  }
}

export default MovieForm;
