import React from "react";
import Form from "./authentication/Form";
import Joi from "joi-browser";
import { getGenres } from "../services/fakeGenreService";
import { getMovie } from "../services/fakeMovieService";

class MovieForm extends Form {
  state = {
    data: { title: "", genreId: "", NumberInStock: "", dialyRentalRate: "" },
    genres: [],
    errors: {},
  };

  schema = {
    _id: Joi.string(),
    title: Joi.string()
      .required()
      .label("Movie title"),
    genreId: Joi.string()
      .required()
      .label("Genre"),
    NumberInStock: Joi.number()
      .required()
      .min(0)
      .max(100)
      .label("Number in stock"),
    dialyRentalRate: Joi.number()
      .required()
      .min(0)
      .max(10)
      .label("Rate"),
  };

  componentDidMount() {
    const genres = getGenres();
    this.setState({ genres });

    const movieId = this.props.match.params.id;
    if (movieId === "new") return;

    const movie = getMovie(movieId);
    if (!movie) return this.props.history.replace("/not-found");

    this.setState({ data: this.mapToViewModel(movie) });
  }

  mapToViewModel = (movie) => {
    return { _id: movie._id };
  };

  doSubmit = () => {
    // Call Server
    console.log("Submited to the server!");
  };

  render() {
    return (
      <div className="w-50 my-0 mx-auto">
        <h2 className="mb-4">Create new movie</h2>
        <form onSubmit={this.handleSubmit}>
          {this.renderInput("title", "Title", true)}
          {this.renderInputSelect("genre", "Genre")}
          {this.renderInput("numberInStock", "Number in stock")}
          {this.renderInput("rate", "Rate")}
          {this.renderButton("Save")}
        </form>
      </div>
    );
  }
}

export default MovieForm;
