import React from "react";

const Input = ({ name, label, value, onChange, error }) => {
  return (
    <div className="form-group">
      <label htmlFor={name}>{label}</label>
      <input
        onChange={onChange}
        name={name}
        value={value}
        id={name}
        className="form-control"
        placeholder={label}
      />
      {error && <p className="text-danger">{error}</p>}
    </div>
  );
};

export default Input;
