import React from "react";
import Joi from "joi-browser";
import Form from "./authentication/Form";
import { getMovie, saveMovie } from "../services/fakeMovieService";
import { getGenres } from "../services/fakeGenreService";

class MovieForm extends Form {
  state = {
    data: { title: "", genreId: "", numberInStock: "", dailyRentalRate: "" },
    genres: [],
    errors: {},
  };

  schema = {
    _id: Joi.string(),
    title: Joi.string()
      .required()
      .label("Title"),
    genreId: Joi.string()
      .required()
      .label("Genre"),
    numberInStock: Joi.number()
      .required()
      .min(0)
      .max(100),
    dailyRentalRate: Joi.number()
      .required()
      .min(0)
      .max(10),
  };

  componentDidMount() {
    const genres = getGenres();
    this.setState({ genres });
    console.log(genres);
  }

  doSubmit = () => {
    // Call Server
    console.log("Login submited to the server!");
  };

  render() {
    const { genres: options } = this.state;
    return (
      <div className="w-50 my-0 mx-auto">
        <h2 className="mb-4">Create Movie</h2>
        <form onSubmit={this.handleSubmit}>
          {this.renderInput("title", "Title", "text", true)}
          {this.renderSelect("genre", "Genre", options)}
          {this.renderInput("numberInStock", "Number in stock")}
          {this.renderInput("dailyRentalRate", "Rate")}
          {this.renderButton("Save")}
        </form>
      </div>
    );
  }
}

export default MovieForm;
