import React, { Component } from "react";
import { getMovies } from "../services/fakeMovieService";
import { getGenres } from "../services/fakeGenreService";
import Like from "./common/Like";
import Pagination from "./common/Pagination";
import { paginate } from "./utilities/paginate";
import ListGroup from "./ListGroup";

class Movies extends Component {
  state = {
    movies: {},
    genres: {},
    selectedGenre: "",
    pageSize: 4,
    currentPage: 1,
  };

  componentDidMount() {
    const genres = [{ name: "All Genres" }, ...getGenres()];
    this.setState({ movies: getMovies(), genres });
  }

  handleGenre = (genre) => {
    this.setState({ selectedGenre: genre });
  };

  handlePageChange = (page) => {
    this.setState({ currentPage: page });
  };

  handleDelete(movie) {
    const movies = this.state.movies.filter((m) => m._id !== movie._id);
    this.setState({
      movies,
    });
  }

  render() {
    const { movies, pageSize, currentPage, genres, selectedGenre } = this.state;
    const filterMovesPerGenre = selectedGenre
      ? movies.filter((m) => m.genre.name === selectedGenre)
      : movies;
    const moviesPerPage = paginate(filterMovesPerGenre, currentPage, pageSize);

    const dbHasMovies = (
      <p>Showing {filterMovesPerGenre.length} movies in database.</p>
    );
    const dbIsEmpty = <p>No movies in databse.</p>;

    return (
      <React.Fragment>
        <div className="d-flex">
          <ListGroup
            genres={genres}
            onGenreChange={this.handleGenre}
            selectedGenre={selectedGenre}
          />
          <div>
            {filterMovesPerGenre.length === 0 ? dbIsEmpty : dbHasMovies}
            <table className="table">
              <thead>
                <tr>
                  <th>Title</th>
                  <th>Genre</th>
                  <th>Stock</th>
                  <th>Rate</th>
                  <th></th>
                  <th></th>
                </tr>
              </thead>
              <tbody>
                {moviesPerPage.map((movie) => (
                  <tr key={movie._id}>
                    <td>{movie.title}</td>
                    <td>{movie.genre.name}</td>
                    <td>{movie.numberInStock}</td>
                    <td>{movie.dailyRentalRate}</td>
                    <td>
                      <Like />
                    </td>
                    <td>
                      <button
                        onClick={() => this.handleDelete(movie)}
                        // onClick={() => this.handleDelete(movie)}  -- This approach is used if the function(handleDelete) is NOT a array function!
                        className="btn btn-danger"
                      >
                        Delete
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
            <Pagination
              itemCount={filterMovesPerGenre.length}
              pageSize={pageSize}
              currentPage={currentPage}
              onPageChange={this.handlePageChange}
            />
          </div>
        </div>
      </React.Fragment>
    );
  }
}

export default Movies;
