import React from "react";

const Input = ({
  name,
  placeholder,
  value,
  id,
  onChange,
  error,
  autoFocus,
  type,
}) => {
  return (
    <div className="form-group">
      <label htmlFor={name}>{placeholder}</label>
      <input
        onChange={onChange}
        name={name}
        value={value}
        id={id}
        autoFocus={autoFocus}
        type={type}
        className="form-control"
        placeholder="Enter email"
      />
      <p className="text-danger">{error}</p>
    </div>
  );
};

export default Input;
